const addTask=document.getElementById("addtask"),toDoListContainer=document.getElementById("todo-list-container");addTask.addEventListener("keypress",(e=>{if("Enter"===e.key)if(0===addTask.value.length)console.log("cant do ant thing");else{const e=document.createElement("div");e.classList.add("todo-list");const t=document.createElement("span");t.classList.add("inline-block","competed-btn");const n=document.createElement("span");n.classList.add("tick-mark","inline-block");const s=document.createElement("img");s.setAttribute("src","dist/images/tick-mark.svg");const o=document.createElement("p");o.classList.add("inline-block","todo-list__text"),o.textContent=addTask.value;const a=document.createElement("span");a.classList.add("inline-block","close-btn");const c=document.createElement("img");c.setAttribute("src","dist/images/close.svg"),toDoListContainer.append(e),e.append(t,o,a),t.append(n),n.append(s),a.append(c),addTask.value="";const d=toDoListContainer.querySelectorAll(".close-btn");for(delectTask of d)delectTask.addEventListener("click",(function(){this.parentElement.remove()}));toDoListContainer.querySelectorAll(".todo-list");const l=toDoListContainer.querySelectorAll(".competed-btn");for(completedTask of l)completedTask.addEventListener("click",(function(){this.parentElement.classList.add("completed")}))}}));